    function hasOrderConfirmationPageBeenReloaded(referer, contextPath) {
        /**
         * In this method we only want the order to be sent to GA once
         * It caters for the following scenarios:
         * 1. When the user got to the confirmation page from the payment page or payu page and the browser detects
         *    whether the page has been reloaded or not
         * 2. When the user takes the confirmation url and opens it in tab in which case the referer will be empty
         * 3. When the user got to the confirmation page via order amend mode in which case the referer will be /checkout/multi/summary/view
         * @type {*[]}
         */

        var split = referer.split(contextPath);
        var serverName = split[0];
        var allowedReferers = [serverName.concat(contextPath).concat("/pnp/en/checkout/multi/payment-method/add"), "https://staging.payu.co.za/"];
        if (performance.navigation.type == performance.navigation.TYPE_RELOAD && allowedReferers.includes(referer)
            || referer == "" || referer.includes(serverName.concat(contextPath).concat("/pnp/en/checkout/multi/summary/view"))) {
            return true;
        } else {
            return false;
        }
    }
